  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
vec4 CCFragOutput (vec4 color) {
  return color;
}
uniform sampler2D cc_spriteTexture;
in vec2 uv0;
layout(std140) uniform Constant {
  vec4 mainColor;
  vec4 strokeColor;
  vec4 fillColor;
  vec4 shaowColor;
  float strokeWidth;
  float shadowWidth;
  float strength;
};
vec4 drawEdgeStroke(){
  vec4 col1 =texture(cc_spriteTexture,uv0);
  if(col1.a>0.2) {
    vec4 col_texture = vec4((texture(cc_spriteTexture,uv0)*strength).rgb,1.);
    #if USE_FILL_COLOR
      col_texture.rgb+=(fillColor.rgb*strength/2.);
    #endif
    return col_texture;
  }
  else {
    #if USE_STROKE
      vec4 col2 = texture(cc_spriteTexture, uv0+vec2(strokeWidth,0.));
      vec4 col3 = texture(cc_spriteTexture, uv0+vec2(-strokeWidth,0.));
      vec4 col4 = texture(cc_spriteTexture, uv0+vec2(0.,strokeWidth));
      vec4 col5 = texture(cc_spriteTexture, uv0+vec2(0.,-strokeWidth));
      vec4 col6 = texture(cc_spriteTexture, uv0+vec2(strokeWidth,strokeWidth));
      vec4 col7 = texture(cc_spriteTexture, uv0+vec2(strokeWidth,-strokeWidth));
      vec4 col8 = texture(cc_spriteTexture, uv0+vec2(-strokeWidth,strokeWidth));
      vec4 col9 = texture(cc_spriteTexture, uv0+vec2(-strokeWidth,-strokeWidth));
      vec4 col_average = (col1+col2+col3+col4+col5+col6+col7+col8+col9)/9.;
      col_average.a = smoothstep(.01,.05,col_average.a);
      if(col_average.a>.05) return strokeColor;
      #if USE_SHAOW
        vec4 col10 = texture(cc_spriteTexture,uv0+vec2(-shadowWidth));
        col10.a = smoothstep(0.2,0.25,col10.a);
        if(col10.a>0.2) return vec4(shaowColor.rgb,col10.a);
      #endif
    #endif
    discard;
  }
}
vec4 frag () {
  vec4 col = drawEdgeStroke();
  return CCFragOutput(col);
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

